// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/// @title Autonomus vehicles ridesharing
/// @author Soultana (Tania) Papasotiriou
/// @notice FOR FUTURE USE  THIS  CONTRACT WILL BE A SEPARATED IN CAR, USER, AND RIDE CONTRACTS
/// @notice You can use this contract for only the most basic ridesharing call
/// @dev All function calls are currently implemented without side effects
/// @custom:experimental This is an experimental contract.

contract AvRide {
    using SafeMath for uint256;

    //CAR VARIABLES

    address payable carAdd;
    mapping(address => bool) private enrolledCar;

    address[] carList;
    uint256 CarID;

    /// @notice vehicle profile
    /// @custom:futureuse additional info can be added like location klm rating functions etc.
    struct CarProf {
        uint256 carID;
        address payable carAdd;
        uint256 pricePerKm;
        uint256 freeSpace; // number of available seating areas in the veicle.
        bool Free;
    }

    CarProf[] public carProfs;

    /// @notice logs for front end
    event LogNewCar(
        uint256 indexed carID,
        address indexed carAdd,
        bool success
    );
    event LogCarProfile(
        uint256 carID,
        address carAdd,
        uint256 pricePerKm,
        uint256 freeSpace,
        bool Free
    );
    event ProfileUpdated(
        uint256 carID,
        address carAdd,
        uint256 pricePerKm,
        uint256 freeSpace,
        bool Free
    );
    event LogSpecificCar(
        uint256 carID,
        address carAdd,
        uint256 pricePerKm,
        uint256 freeSpace,
        bool Free
    );
    event LogCarLists(
        uint256 carID,
        address carAdd,
        uint256 pricePerKm,
        uint256 freeSpace,
        bool Free
    );

    //CAR FUNCTIONS

    /// @notice Checks if vehicle exists in array a.k.a already enrolled
    ///@dev returns bool
    function isCarNew(address _carAdd) private returns (bool Exists) {
        require(enrolledCar[_carAdd] != true, "car already enrolled");

        enrolledCar[_carAdd] = true;
        return (enrolledCar[_carAdd]);
    }

    /// @notice Registrers a vehicle
    function enrollCar(address payable _carAdd) public {
        require(isCarNew(_carAdd) == true, "car already enrolled"); /// @notice car should not exist in array

        carAdd = _carAdd; /// @notice set the car address
        enrolledCar[_carAdd] = true; /// @notice change the status
        carList.push(_carAdd);

        CarID = carList.length - 1; /// @notice set registration ID
        emit LogNewCar(CarID, _carAdd, enrolledCar[_carAdd]);
    }

    /// @notice Sets vehicles Ride parameters
    /// @dev This should run after straight enrollment.
    /// @custom:futureuse it will be posible to select vehicle from enrolement list

    function setCarProfile(
        address payable _carAdd,
        uint256 _pricePerKm,
        uint256 _freeSpace,
        bool _Free
    ) public {
        require(carAdd == _carAdd, "car is not enrolled");

        CarProf memory carProf;

        carProf.carID = CarID;
        carProf.carAdd = _carAdd;
        carProf.pricePerKm = _pricePerKm;
        carProf.freeSpace = _freeSpace;
        carProf.Free = _Free;

        ///@notice in case of no free space but vehicle still declared as free.
        if (carProf.freeSpace == 0 && carProf.Free == true) {
            revert("vehicle is not free");
        }

        emit LogCarProfile(CarID, carAdd, _pricePerKm, _freeSpace, _Free);

        carProfs.push(carProf);
    }

    /// @notice updates vehicles state after ride
    function updateCarInfo(
        uint256 _carIndex,
        uint256 _pricePerKm,
        uint256 _freeSpace,
        bool _Free
    ) private {
        require(_carIndex <= CarID, "Invalid registration number");

        CarProf storage carProf = carProfs[_carIndex];

        carProf.pricePerKm = _pricePerKm;
        carProf.freeSpace = _freeSpace;
        carProf.Free = _Free;

        emit ProfileUpdated(CarID, carAdd, _pricePerKm, _freeSpace, _Free);
    }

    //USER VARIABLES

    address public user = msg.sender;

    /// @custom:futureuse the ride might be on behalf of someone
    mapping(address => bool) private userCalledRide;
    uint256 ScarID;
    address payable ScarAdd;
    uint256 ScarPrice;
    uint256 ScarSpace;
    bool ScarFree;

    /// @custom:futureuse other criteria like location rating etc will be in the struct.
    struct RideProfile {
        uint256 seatNo;
    }

    RideProfile[] internal rideProfiles;

    /// @notice logs for front end
    event LogCallRide(address accountAddress);
    event LogRidePayment(address accountAddress, uint256 ridefee);
    event LogRideInfo(
        uint256 carID,
        address SCarAdd,
        uint256 ridePrice,
        uint256 seatNo,
        uint256 freeSpace,
        bool Free
    );
    event LogRideUpdated(uint256 _seatNo);

    modifier onlyUser() {
        require(userCalledRide[msg.sender] == true);
        _;
    }

    //USER FUNCTIONS

    /// @notice the user enrols by stating the number of required seats for the ride
    /// @custom:futureuse this enrollement addresses  might be stored in an array keeping log on the users
    /// @custom:futureuse several criteria might be added like from location, to location, rating preference etc.
    /// @dev returns bool
    function enrollUser(uint256 _seatNo) public returns (bool) {
        if (_seatNo <= 0) {
            revert("you must select seat number");
        }

        userCalledRide[msg.sender] = true;

        rideProfiles.push(RideProfile(_seatNo));

        /// @dev in case the user changes number of requires seats
        updateRideInfo(_seatNo);

        emit LogCallRide(msg.sender);
        return userCalledRide[msg.sender];
    }

    /// @notice returns all registered cars
    /// @dev returns tuple
    function getCarProfileList() public //returns (CarProf[] memory)
    {
        //i feel that this is not the most optimum way but this is what i came out with
        //event  for Car Lists  append entries by  looping through the list
        for (uint256 i = 0; i <= CarID; i++) {
            CarProf storage carProf = carProfs[i];
            emit LogCarLists(
                i,
                carProf.carAdd,
                carProf.pricePerKm,
                carProf.freeSpace,
                carProf.Free
            );
        }
        //return carProfs;
    }

    /// @notice select the car and review the ride preferences
    /// @dev returns checking values of price and address
    function selectCarReviewRide(uint256 _carID)
        public
        returns (uint256, address)
    {
        /// @dev futureuse in case were the caller ight neet to call several rides and 0 might chanje with rideIndex
        RideProfile memory rideProfile = rideProfiles[0];
        CarProf memory carProf = carProfs[_carID];

        require(carProf.Free == true, "the car is not available");
        require(
            rideProfile.seatNo <= carProf.freeSpace,
            "there is not enough space in the car"
        );

        ScarID = _carID;
        ScarAdd = carProf.carAdd;
        ScarPrice = carProf.pricePerKm;
        ScarSpace = carProf.freeSpace;
        ScarFree = carProf.Free;

        emit LogRideInfo(
            _carID,
            ScarAdd,
            ScarPrice,
            rideProfile.seatNo,
            carProf.freeSpace,
            carProf.Free
        );

        return (ScarPrice, ScarAdd);
    }

    // RIDE VARIABLES

    bool confirmedByCar;
    bool refundState;

    uint256 CancelFee;
    uint256 refund;

    /// @notice the states of the ride
    enum RideStatus {
        Free,
        Confirmed,
        Started
    }
    RideStatus public status;

    /// @custom:futureuse some additional confiramtion criteria might be added
    constructor() {
        user = payable(msg.sender);
    }

    /// @notice required for escrow
    mapping(address => uint256) public deposits;

    /// @notice logs for front end
    event TxConfirmed(uint256 deposit);
    event RideConfirmation(bool rideIsConfirmed, RideStatus);
    event RideStarts(RideStatus);
    event ride_carUpdate(
        uint256 ScarID,
        uint256 ScarPrice,
        uint256 Rspace,
        bool Rfree
    );
    event Revert_CarUpdate(
        uint256 ScarID,
        uint256 ScarPrice,
        uint256 Rspace,
        bool Rfree
    );
    event RideCompletes(RideStatus);
    event TxReceived(uint256 payment, address SCar);
    event RideCanceled(uint256 payment, RideStatus);
    event UserRefunded(uint256 payment);
    event StatusReset(bool confirmedByCar);

    // RIDE FUNCTIONS

    /// @notice confirms the ride and holds a deposit in escrow
    /// @dev payment in wei
    function confirmRide() public payable onlyUser {
        require(msg.value >= ScarPrice, "please check your balance");
        if (carConfirmation() != true) {
            revert("ride is not confirmed");
        }

        refundState = false; /// @notice resets refund state

        updateRideState(); /// @notice reserves the requested seats

        deposits[ScarAdd] += ScarPrice; /// @notice pays in wei

        if (deposits[ScarAdd] < ScarPrice) {
            revert("please review the deposit");
        }

        emit TxConfirmed(deposits[ScarAdd]);
    }

    /// @custom:futureuse car will need to confirm taking into acount direction and location
    /// @notice change Car confiramtion state
    /// @notice this might prove useful in case of simultaneus confirmations
    /// @dev returns bool
    function carConfirmation() internal returns (bool) {
        RideProfile memory rideProfile = rideProfiles[0];
        require(ScarSpace >= rideProfile.seatNo, "car is not confirming");

        confirmedByCar = true;

        status = RideStatus.Confirmed;

        emit RideConfirmation(confirmedByCar, status);

        return (confirmedByCar);
    }

    /// @notice starts the ride
    function startCarRide() public onlyUser {
        require(status == RideStatus.Confirmed, "ride in not confirmed");
        require(refundState != true, "ride was refunded");
        status = RideStatus.Started;

        emit RideStarts(status);
    }

    /// @notice completes the ride and transfers payment from escrow
    function conmpleteCarRide() public onlyUser {
        require(status == RideStatus.Started, "ride is not active");

        /// @notice payment tnx
        uint256 transferedFee = deposits[ScarAdd];
        ScarAdd.transfer(transferedFee);
        deposits[ScarAdd] = 0;

        emit TxReceived(transferedFee, ScarAdd);

        /// @notice change the ride status
        status = RideStatus.Free;
        updateRideState();

        /// @notice refund the user in case of bigger deposit value
        userRefund();

        emit RideCompletes(status);
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return a / b;
    }

    /// @notice cancels the ride
    function RideIsCanceled() public onlyUser {
        if (status == RideStatus.Free) {
            revert("Ride has already completed or canceled");
        }

        /// @notice in case of cancelation after starting the ride
        /// @custom:futureuse this canselation will be associated with the covered distance
        if (status == RideStatus.Started) {
            /// @notice  assumption 1/2 of the price penalty in case of canselation before start
            /// @custom:futureuse the distance covered until cancelation
            CancelFee = deposits[ScarAdd].div(2);

            /// @notice transfer fee
            deposits[ScarAdd] = 0;
            ScarAdd.transfer(CancelFee);
        } else if (status == RideStatus.Confirmed) {
            /// @notice assumption 1/4 of the price penalty in case of canselation before start
            /// @custom:futureuse the distance between car location and from User location
            CancelFee = deposits[ScarAdd].div(4);

            /// @notice transfer fee
            deposits[ScarAdd] = 0;
            ScarAdd.transfer(CancelFee);
        }

        /// @notice revert vehicle and ride state
        status = RideStatus.Free;
        updateRideState();

        ///@notice refund user
        userRefund();

        emit RideCanceled(CancelFee, status);
    }

    // HELP FUNCTIONS

    /// @notice update ride info registration
    function updateRideInfo(uint256 _seatNo) private {
        /// @dev futureuse 0=index when we store rides
        RideProfile storage rideProfile = rideProfiles[0];
        if (rideProfile.seatNo != _seatNo) {
            rideProfile.seatNo = _seatNo;
        }
        emit LogRideUpdated(_seatNo);
    }

    ///@notice returns remaining value to users account
    function userRefund() public payable onlyUser returns (bool) {
        /// @notice to avoid refund before completion
        /// @custom:futureuse this will work in assosiatin with location and distance covered
        require(
            status == RideStatus.Confirmed || status == RideStatus.Free,
            " currently you cannot refund"
        );

        /// @notice get the amount of Ether stored in this contract
        refund = address(this).balance;

        (bool success, ) = user.call{value: refund}("");
        require(success, "Failed to send Ether");

        /// @notice reset the ride state
        ResetRide();
        refundState = true;

        emit UserRefunded(refund);
        return (refundState);
    }

    ///@notice updates the car state and available seats after starting  the ride
    function updateRideState() private {
        RideProfile memory rideProfile = rideProfiles[0];
        CarProf memory carProf = carProfs[ScarID];

        uint256 Rspace;
        bool Rfree;

        /// @notice returns state and seats when ride is canceled or completed
        if (status == RideStatus.Free) {
            Rspace = carProf.freeSpace.add(rideProfile.seatNo);
            Rfree = true;

            /// @notice returns state and seats when ride is confirmed
        } else if (status == RideStatus.Confirmed) {
            Rspace = ScarSpace.sub(rideProfile.seatNo);
            Rfree;

            if (rideProfile.seatNo < ScarSpace) {
                Rfree = true;
            } else {
                Rfree = false;
            }
        }

        // @notice uodates the Vehicle ride status
        updateCarInfo(ScarID, ScarPrice, Rspace, Rfree);
        confirmedByCar = false;

        emit Revert_CarUpdate(ScarID, carProf.pricePerKm, Rspace, Rfree);
    }

    /// @notice resets cars confirmation
    function ResetRide() internal onlyUser {
        confirmedByCar = false;
        emit StatusReset(confirmedByCar);
    }

    receive() external payable {
        revert();
    }

    fallback() external payable {
        revert();
    }
}

